import React, { useState } from 'react';

function App() {
  const [result, setResult] = useState('0');

  const handleClick = (value) => {
    setResult((prevResult) => {
      if (value === 'AC') return '0';

      if (prevResult === '0') {
        return value;
      } else {
        return prevResult + value;
      }
    });
  };

  const calculateResult = () => {
    try {
      setResult((prevResult) => eval(prevResult).toString());
    } catch (error) {
      setResult('Error');
    }
  };

  return (
    <div className="w-98 mx-auto border border-gray-300 rounded-md p-10">
      <div className="bg-gray-200 border-4 border-gray-300 rounded-md p-10 text-right text-24 mb-10">
        {result}
      </div>
      <div className="grid grid-cols-4 gap-10">
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('7')}>
          7
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('8')}>
          8
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('9')}>
          9
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('/')}>
          รท
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('4')}>
          4
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('5')}>
          5
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('6')}>
          6
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('*')}>
          *
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('1')}>
          1
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('2')}>
          2
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('3')}>
          3
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('-')}>
          -
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('0')}>
          0
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('.')}>
          .
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => calculateResult()}>
          =
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('+')}>
          +
        </button>
        <button className="p-10 text-18 rounded-md border-none bg-gray-100 cursor-pointer" onClick={() => handleClick('AC')}>
          AC
        </button>
      </div>
    </div>
  );
}

export default App;
